// components/AlterDocentes.tsx

import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, Button, TextInput, Modal, Alert } from 'react-native';
import { supabase } from '../supabaseClient';

// Definição do tipo Docente
type Docente = {
  ID_Docente: number;
  Nome: string;
  Regime: number;
  Ordem: number;
  updated_at: string;
  is_deleted: boolean;
  created_at: string;
};

const AlterDocentes = () => {
  const [docentes, setDocentes] = useState<Docente[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState<string>('null');

  // Estados do Modal para adicionar docente
  const [modalVisible, setModalVisible] = useState<boolean>(false);
  const [nome, setNome] = useState<string>('');
  const [regime, setRegime] = useState<string>('');

  useEffect(() => {
    fetchDocentes();
  }, []);

  const fetchDocentes = async () => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from<Docente>('Docentes')
        .select('*')
        .neq('is_deleted', true)
        .order('ID_Docente', { ascending: true });

      if (error) {
        throw error;
      }

      setDocentes(data || []);
    } catch (err: any) {
      console.error('Erro ao buscar docentes:', err.message);
      setError('Não foi possível carregar os docentes.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddDocente = async () => {
    if (!nome || !regime) {
      Alert.alert('Erro', 'Por favor, preencha todos os campos.');
      return;
    }

    try {
      const regimeInt = parseInt(regime);
      if (isNaN(regimeInt)) {
        Alert.alert('Erro', 'Regime deve ser um número válido.');
        return;
      }

      const novaOrdem = docentes.length > 0 ? Math.max(...docentes.map(d => d.Ordem)) + 1 : 1;
      const novoID_Docente = docentes.length > 0 ? Math.max(...docentes.map(d => d.ID_Docente)) + 1 : 1;

      const { error } = await supabase
        .from('Docentes')
        .insert([{ ID_Docente: novoID_Docente, Nome: nome, Regime: regimeInt, Ordem: novaOrdem, is_deleted: false}]);

      if (error) {
        throw error;
      }

      Alert.alert('Sucesso', 'Docente adicionado com sucesso.');
      setNome('');
      setRegime('');
      setModalVisible(false);
      fetchDocentes();
    } catch (err: any) {
      console.error('Erro ao adicionar docente:', err.message);
      Alert.alert('Erro', 'Não foi possível adicionar o docente.');
    }
  };

  const handleDeleteDocente = async (id: number) => {
    try {
        const { error } = await supabase
        .from('Docentes')
        .update({ is_deleted: true })
        .eq('ID_Docente', id);

      if (error) {
        throw error;
      }
      Alert.alert('Sucesso', 'Docente excluído com sucesso.');
      fetchDocentes();
    } catch (err: any) {
      console.error('Erro ao excluir docente:', err.message);
      Alert.alert('Erro', 'Não foi possível excluir o docente.');
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <Text>Carregando docentes...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Alterar Docentes</Text>
      <TextInput
        style={styles.input}
        placeholder="Pesquise"
        value={search}
        onChangeText={(text) => setSearch(text)}
      />
      <Button title="Adicionar Docente" color="#0D92F4" onPress={() => setModalVisible(true)} />

      <FlatList
        data={docentes}
        keyExtractor={(item) => item.ID_Docente.toString()}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text style={styles.nome}>{item.Nome}</Text>
            <Text>Regime: {item.Regime} horas</Text>
            <Text>Ordem: {item.Ordem}</Text>
            <View style={styles.actions}>
              <Button title="Excluir" color="#FF4545" onPress={() => handleDeleteDocente(item.ID_Docente)} />
            </View>
          </View>
        )}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
      />

      {/* Modal para adicionar docente */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalView}>
            <Text style={styles.modalTitle}>Adicionar Docente</Text>

            <TextInput
              style={styles.input}
              placeholder="Nome do Docente"
              value={nome}
              onChangeText={(text) => setNome(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="Regime (horas)"
              value={regime}
              onChangeText={(text) => setRegime(text)}
              keyboardType="numeric"
            />

            <Button title="Adicionar" color="#0D92F4" onPress={handleAddDocente} />
            <Button title="Cancelar" color="#888" onPress={() => setModalVisible(false)} />
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    paddingTop: 50,
    backgroundColor: '#f5f5f5',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
    color: '#384B70',
  },
  item: {
    backgroundColor: '#ffffff',
    padding: 15,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
  },
  nome: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 5,
    color: '#333333',
  },
  separator: {
    height: 10,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  errorText: {
    color: 'red',
    fontSize: 16,
    textAlign: 'center',
  },
  actions: {
    flexDirection: 'row',
    justifyContent: 'flex-end',
    marginTop: 10,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0,0,0,0.5)',
  },
  modalView: {
    width: 300,
    padding: 20,
    backgroundColor: 'white',
    borderRadius: 10,
    elevation: 5,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 15,
    textAlign: 'center',
  },
  input: {
    borderWidth: 1,
    borderColor: '#999',
    borderRadius: 8,
    padding: 10,
    marginBottom: 15,
  },
});

export default AlterDocentes;
