// App.tsx

import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, ActivityIndicator, Button } from 'react-native';
import { supabase } from './supabaseClient'; // Certifique-se de que o caminho está correto
import { Provider as PaperProvider } from 'react-native-paper';

// Definição do tipo Docente
type Docente = {
  ID_Docente: number;
  Nome: string;
  Regime: number;
  Ordem: number;
  updated_at: string;
  is_deleted: boolean;
  created_at: string;
};

const App = () => {
  const [docentes, setDocentes] = useState<Docente[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchDocentes = async () => {
      try {
        const { data, error } = await supabase
          .from<Docente>('Docentes')
          .select('*')
          .neq('is_deleted', true)
          .order('ID_Docente', { ascending: true });

        if (error) {
          throw error;
        }

        setDocentes(data || []);
      } catch (err: any) {
        console.error('Erro ao buscar docentes:', err.message);
        setError('Não foi possível carregar os docentes.');
      } finally {
        setLoading(false);
      }
    };

    fetchDocentes();
  }, []);

  if (loading) {
    return (
      <PaperProvider>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#6200ee" />
          <Text>Carregando docentes...</Text>
        </View>
      </PaperProvider>
    );
  }

  if (error) {
    return (
      <PaperProvider>
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
        </View>
      </PaperProvider>
    );
  }

  return (
    <PaperProvider>
      <View style={styles.container}>
        <Text style={styles.title}>Lista de Docentes</Text>
        <FlatList
          data={docentes}
          keyExtractor={(item) => item.ID_Docente.toString()}
          renderItem={({ item }) => (
            <View style={styles.item}>
              <Text style={styles.nome}>{item.Nome}</Text>
              <Text>Carga Horária: {item.Regime} horas</Text>
              <Text>Ordem: {item.Ordem}</Text>
              <View style={styles.actions}>
                <Button title="Alterar" color="#0D92F4"/>
                <Button title="Excluir" color="#FF4545"/>
              </View>
            </View>
          )}
          ItemSeparatorComponent={() => <View style={styles.separator} />}
        />
      </View>
    </PaperProvider>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    paddingTop: 50,
    backgroundColor: '#f5f5f5',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
    color: '#6200ee',
  },
  item: {
    backgroundColor: '#ffffff',
    padding: 15,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
  },
  nome: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 5,
    color: '#333333',
  },
  separator: {
    height: 10,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  errorText: {
    color: 'red',
    fontSize: 16,
    textAlign: 'center',
  },

});

export default App;
