import React, {useEffect, useState} from 'react';
import {View, Text, FlatList, StyleSheet, ActivityIndicator, Button, TextInput, Modal, Alert} from 'react-native';
import {supabase} from '../supabaseClient';
import {Provider as Paper, PaperProvider} from 'react-native-paper';

type Docente = {
    ID_Docente: number;
    Nome: string;
    Regime: number;
    Ordem: number;
    update_at: string;
    is_deleted: boolean;
    created_at: string;
};

const ListDocentes = () => {
    const [docentes, setDocentes] = useState<Docente[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string|null>(null);

    useEffect(() => {
        listDocentes();
    }, []);

    const listDocentes = async () => {
        try{
            setLoading(true);
            const {data, error} = await supabase//espere o supabase
            .from('Docentes')//nome da tabela
            .select('*')//seleciona todos
            .neq('is_deleted', true)//exceto os deletados
            .order('ID_Docente', {ascending:true});//arruma em ordem crescente do ID

            if(error){
                throw error;
            }

            setDocentes(data||[]);
        }catch(err:any){
            console.error('Erro ao buscar docentes: ', err.message);
            setError('Não foi possível carregar os docentes.');
        }finally{
            setLoading(false);
        }
    };

    return(
        <Paper>
            <View style={styles.view}>
                <Text style={styles.text}>Lista de Docentes</Text>
                    <FlatList
                        data={docentes}
                        keyExtractor={(item) => item.ID_Docente.toString()}
                        renderItem={({item}) => (
                            <View style={styles.item}>
                                <Text style={styles.nome}>{item.Nome}</Text>
                                <Text style={styles.regime}>{item.Regime} horas</Text>
                                <Text style={styles.ordem}>{item.Ordem}</Text>
                            </View>
                        )}
                            ItemSeparatorComponent={() => <View style={styles.separator}/>}
                    />
            </View>
        </Paper>
    );
};

const styles = StyleSheet.create({
    view:{

    },
    text:{

    },
    item:{

    },
    nome:{

    },
    regime:{

    },
    ordem:{

    },
    separator:{

    },
});

export default ListDocentes;
